graphql.query:
query {
  login(username: "admin", password: "admin123") {
    token
    user {
      id
      username
      role
    }
  }
}

query {
  listEmployeesPaginated(page: 1, limit: 10, sortBy: "age", sortOrder: ASC) {
    data {
      id
      name
      age
      subjects
      attendance {
        status
        date
      }
    }
    pagination {
      page
      limit
      total
      totalPages
      hasNextPage
      hasPreviousPage
    }
  }
}

query GetEmployee {
	getEmployee(id: "1") {
		id
		name
		age
		class
		subjects
		attendance {
			date
			status
		}
	}
}

mutation AddNewEmployee {
  addEmployee(
    input: {
      name: "Camille Johnson"
      age: 23
      class: "12th"
      subjects: ["Physics", "Chemistry", "Biology", "Mathematics"]
      attendance: [
        { date: "2024-01-20", status: "present" }
        { date: "2024-01-21", status: "present" }
        { date: "2024-01-22", status: "late" }
      ]
    }
  ) {
    id
    name
    age
    class
    subjects
    attendance {
      date
      status
    }
  }
}

mutation UpdateEmployee {
	updateEmployee(
		id: "1"
		input: {
			name: "John Doe Updated"
			age: 26
			class: "10th"
			subjects: ["Math", "Science", "English", "Computer Science"]
			attendance: [
				{ date: "2024-01-15", status: "present" }
				{ date: "2024-01-16", status: "present" }
				{ date: "2024-01-17", status: "present" }
			]
		}
	) {
		id
		name
		age
		class
		subjects
		attendance {
			date
			status
		}
	}
}
